import React, { useState } from "react"
import './App.css'
import AddTask from "./components/addTask"
import Tasks from "./components/Tasks"

const App = () => {
  const [tasks, setTasks] = useState([
    {
        id: '1',
        title: 'Estudar hoje',
        completed: false,
    },
    {
      id: '2',
      title: 'Ler Livros',
      completed: true,
  }
  ])

  const handleTaskEdition = (taskTitle) => {
    const newTask = [... tasks,
      {
        title: taskTitle,
        id: Math.random(10),
        completed: false
      }
    ]
    setTasks(newTask)
  }

  return ( 
    <> 
      <div className="container">
        <AddTask />
        <Tasks tasks={tasks}/>
      </div>
    </> 
  )


}

export default App
