import React from 'react';
import './Task.css';


const Task = ({ task, handleTaskClick }) => {
    return (
        <>
            <div className="task-container" style={task.completed ? {borderLeft: '5px solid rebeccapurple'} : {}}>
                <div className="task-title" onClick={handleTaskClick}>
                    {task.ti}
                </div>
            </div>
        </>
    );
}
 
export default Task;
